document.addEventListener("DOMContentLoaded",()=>{console.log("Document ready");const e=document.getElementById("announcement-bar");if(e){console.log("Announcement bar found");const n=e.querySelector(".announcement-container");if(n){console.log("Container found");const o=n.querySelectorAll(".announcement-message");let t=0;o.forEach(e=>{t+=e.offsetWidth}),console.log("Total width:",t);const s=gsap.to(n,{x:-t,duration:50,ease:"none",repeat:-1,onRepeat:()=>{gsap.set(n,{x:0})},onStart:()=>{const e=(n.parentElement.offsetWidth-n.offsetWidth)/2;gsap.set(n,{x:e})}});e.addEventListener("mouseenter",()=>{s.pause()}),e.addEventListener("mouseleave",()=>{s.play()})}else console.log("Container not found")}else console.log("Announcement bar not found")});